<script lang="ts">
	import { onMount } from 'svelte';
	import { browser } from '$app/environment';
	let gsap: any;
	let ScrollTrigger: any;

	let svgContainer: HTMLDivElement;
	let { optionIndex }: { optionIndex: number } = $props();

	const svgOptions = [
		'/images/boat-drawing.svg',
		'/images/mountain-drawing.svg',
		'/images/city-drawing.svg'
	];

	onMount(async () => {
		if (browser) {
			const gsapModule = await import('gsap');
			const scrollTriggerModule = await import('gsap/ScrollTrigger');

			gsap = gsapModule.default;
			ScrollTrigger = scrollTriggerModule.default;
			gsap.registerPlugin(ScrollTrigger);

			// Get all paths and sort by length
			const paths = Array.from(svgContainer.querySelectorAll('path')) as SVGPathElement[];

			// Sort paths by length (longest first) to ensure main outlines are drawn first
			const sortedPaths = paths.sort((a, b) => b.getTotalLength() - a.getTotalLength());

			// Set initial state for all paths
			sortedPaths.forEach((path) => {
				const length = path.getTotalLength();
				gsap.set(path, {
					strokeDasharray: length,
					strokeDashoffset: length,
					fill: 'none',
					stroke: path.getAttribute('fill') || 'currentColor'
				});
			});

			// Create timeline for sequential path drawing
			const tl = gsap.timeline({
				scrollTrigger: {
					trigger: svgContainer,
					start: 'top bottom-=200',
					end: 'bottom center',
					toggleActions: 'play none none reverse'
				}
			});

			// Draw all paths with stagger
			tl.to(sortedPaths, {
				strokeDashoffset: 0,
				duration: 1,
				stagger: 0.3,
				ease: 'power1.inOut'
			}).to(
				sortedPaths,
				{
					fill: (i: number) => sortedPaths[i].getAttribute('fill') || 'currentColor',
					stroke: 'none',
					duration: 1,
					stagger: 0.4,
					ease: 'power1.inOut'
				},
				'-=0.5'
			);
		}
	});
</script>

