<script lang="ts">
	import { onMount } from 'svelte';
	import { browser } from '$app/environment';

	let gsap: any;
	let ScrollTrigger: any;
	let svgContainer: HTMLDivElement;

	let { optionIndex }: { optionIndex: number } = $props();

	onMount(async () => {
		if (browser) {
			const gsapModule = await import('gsap');
			const scrollTriggerModule = await import('gsap/ScrollTrigger');

			gsap = gsapModule.default;
			ScrollTrigger = scrollTriggerModule.default;
			gsap.registerPlugin(ScrollTrigger);

			// Get all paths and sort them by x position
			const paths = Array.from(svgContainer.querySelectorAll('.letter-path')) as SVGPathElement[];
			const highlightPath = svgContainer.querySelector('.highlight-path') as SVGPathElement;

			// Set initial state for all paths
			paths.forEach((path) => {
				const length = path.getTotalLength();
				gsap.set(path, {
					strokeDasharray: length,
					strokeDashoffset: length,
					fill: 'none',
					stroke: 'currentColor'
				});
			});

			// Set initial state for highlight
			if (highlightPath) {
				const length = highlightPath.getTotalLength();
				gsap.set(highlightPath, {
					strokeDasharray: length,
					strokeDashoffset: length,
					opacity: 0
				});
			}

			// Create timeline for sequential animation
			const tl = gsap.timeline({
				scrollTrigger: {
					trigger: svgContainer,
					start: 'top center',
					toggleActions: 'play none none reverse'
				}
			});

			// Add signature paths to the timeline with a stagger
			paths.forEach((path, index) => {
				tl.to(
					path,
					{
						strokeDashoffset: 0,
						duration: 0.6,
						ease: 'power2.out'
					},
					index * 0.15
				);
			});

			// Then animate the highlight
			if (highlightPath) {
				tl.to(
					highlightPath,
					{
						opacity: 0.2,
						duration: 0.1
					},
					'>'
				).to(
					highlightPath,
					{
						strokeDashoffset: 0,
						duration: 0.8,
						ease: 'power1.inOut'
					},
					'<'
				);
			}
		}
	});
</script>

